import _ from 'lodash'
import { 
    READ_EVENTS,
    DELETE_EVENT,
    GET_EVENT
} from '../actions'

export default (events = {}, action) => {
    switch (action.type) {
        case READ_EVENTS:
            return _.mapKeys(action.response.data, 'id')
        case GET_EVENTS:
            console.log(GET_EVENT)
            return _.mapKeys(action.response.data, 'id')
        case DELETE_EVENT:
            //delete完了後のイベントを返す
            delete events[action.id]
            return { ...events}
        default:
            return events
    }
}
