import React, { Component } from 'react';
import { connect } from 'react-redux';
import { increment, decrement } from '../actions'
//import PropTypes from 'prop-types';

class App extends Component {
  //reduxにおいては、reducerで行うので、不要になる。
  //hundlePlusButton, hundleMinusButtonはaction createrで行うので不要

  // constructor(props) {
  //   super(props)
  //   this.state = {count : 0}
  // }

  // //setStateが実行されると、コールバックでrender()が実行される。
  // hundlePlusButton = () => {
  //   this.setState({count : this.state.count + 1})
  // }

  // hundleMinusButton = () => {
  //   this.setState({count : this.state.cuont - 1})
  // }

  render() {
    const props = this.props

    return (
      <div>
        <div>value : {props.value}</div>
        <button onClick={props.increment}>+1</button>
        <button onClick={props.decrement}>-1</button>
      </div>
    )
  }
}

//stateとactioをcomponentに関連づけるコーディング　start
//このコンポーネントに必要なものをstateから取り出して、どのオブジェクトをcomponentのpropsとして対応させるのかを関数の戻り値で定義
const mapStateToProps = state => ({value: state.count.value})
//あるアクションが発生した際に、reducerに状態に応じてActionを実行させる
const mapDispatchToProps = dispatch => ({
  increment: () => dispatch(increment()),
  decrement: () => dispatch(decrement())
})

export default connect(mapStateToProps, mapDispatchToProps)(App)
